mapper.select:
SELECT o.id, h.name as house_name, COALESCE(hi.url, h.image) as house_image, o.check_in, o.check_out, o.nights, o.guests, o.amount, o.status, o.create_time FROM t_user_order o LEFT JOIN t_house h ON o.house_id = h.id LEFT JOIN t_house_image hi ON h.id = hi.house_id AND hi.is_cover = 1 AND hi.deleted = 0 WHERE o.user_id = #{userId}
AND o.status = #{status}
ORDER BY o.create_time DESC
