mapper.select:
select
id, house_id, status, booking_start, booking_end, user_name, phone, email, created_at, updated_at
from t_house_booking


        and id = #{id}


        and house_id = #{houseId}


        and status = #{status}


        and booking_start = #{bookingStart}


        and booking_end = #{bookingEnd}


        and user_name = #{userName}


        and phone = #{phone}


        and email = #{email}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_house_booking


        and id = #{id}


        and house_id = #{houseId}


        and status = #{status}


        and booking_start = #{bookingStart}


        and booking_end = #{bookingEnd}


        and user_name = #{userName}


        and phone = #{phone}


        and email = #{email}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
